// Helper functions
function slugify(str) {
  return str.toLowerCase()
    .replace(/[^a-z0-9]+/g, '-')
    .replace(/(^-|-$)/g, '');
}

function escapeHtml(str) {
  return (str || '').toString()
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;')
    .replace(/"/g, '&quot;')
    .replace(/'/g, '&#039;');
}

function norm(s) {
  return (s ?? "").toString().toLowerCase().trim();
}

function pricingLabel(pricing) {
  if (!pricing || norm(pricing) === "unknown") return "";
  const p = norm(pricing);
  if (p === "free") return "Free";
  if (p === "freemium") return "Freemium";
  if (p === "paid") return "Paid";
  if (p === "enterprise") return "Enterprise";
  return pricing;
}

