// Check if tool is available on fal.ai (based on naming patterns)
function isFalAiTool(tool) {
  const falAiIndicators = [
    'kandinsky', 'wan', 'hunyuan', 'ltx', 'seedance', 'vidu', 'gpt-image',
    'flux', 'stable-diffusion', 'bria', 'chatterbox', 'trellis', 'lightx',
    'minimax', 'stable-audio', 'elevenlabs', 'lyria', 'sonauto', 'imagen',
    'recraft', 'ideogram', 'ovis', 'longcat', 'bagel', 'kling', 'ray2',
    'hailuo', 'omnihuman', 'pixverse', 'runway-gen-3'
  ];
  const toolId = norm(tool.id);
  return falAiIndicators.some(indicator => toolId.includes(indicator));
}

function generateMetaTags(tool) {
  const modality = tool.modalities?.[0] || 'AI tool';
  const modalityLabel = getModalityLabel(modality);
  
  // Enhanced SEO description with value proposition
  let description = tool.whatItDoes || '';
  if (tool.whyPicked) {
    description += ` ${tool.whyPicked}`;
  }
  
  // Add accessibility context for image tools
  const isImageTool = tool.modalities?.some(m => m.includes('image'));
  if (isImageTool && description.length < 140) {
    description += ' Professional image creation with AI-powered tools.';
  }
  
  // Add alternative context keywords
  const alternativeKeywords = [];
  if (isImageTool) {
        alternativeKeywords.push('photoshop alternative', 'ai image generator', 'ai design tools');
  }
  if (tool.modalities?.some(m => m.includes('video'))) {
    alternativeKeywords.push('video editing alternative', 'ai video generator');
  }
  if (tool.modalities?.some(m => m.includes('audio'))) {
    alternativeKeywords.push('music production alternative', 'ai audio generator');
  }
  
  const keywords = [
    tool.name,
    ...(tool.modalities || []),
    ...alternativeKeywords,
    'ai tools',
    'generative ai',
    'curated ai tools'
  ].join(', ');
  
  // Enhanced title with value proposition
  let title = `${tool.name} - ${modalityLabel} AI Tool`;
  if (isImageTool && tool.pricing === 'free' || tool.pricing === 'freemium') {
    title += ' | Free Alternative';
  }
  title += ' | AI Tool Directory';
  
  const metaDesc = description.substring(0, 155);
  const ogDesc = description.substring(0, 200);
  
  return `
    <title>${escapeHtml(title)}</title>
    <meta name="description" content="${escapeHtml(metaDesc)}">
    <meta name="keywords" content="${escapeHtml(keywords)}">
    <link rel="canonical" href="https://curatedai.net/tools/${tool.id}.html">
    <meta property="og:title" content="${escapeHtml(title)}">
    <meta property="og:description" content="${escapeHtml(ogDesc)}">
    <meta property="og:type" content="website">
    <meta property="og:url" content="https://curatedai.net/tools/${tool.id}.html">
    <meta name="twitter:card" content="summary_large_image">
    <meta name="twitter:title" content="${escapeHtml(tool.name)}">
    <meta name="twitter:description" content="${escapeHtml(ogDesc)}">
  `;
}

