// Generate detailed explanation from summary
function generateNewsExplanation(newsItem) {
  const summary = newsItem.summary || '';
  const title = newsItem.title || '';
  
  // Extract key topics from title and summary
  const topics = [];
  if (title.includes('Chip Tariffs') || title.includes('Tariff')) {
    topics.push('US chip tariff policies');
  }
  if (title.includes('ServiceNow') || title.includes('$7.75B') || title.includes('$7B')) {
    topics.push('ServiceNow enterprise AI deals');
  }
  if (title.includes('Prompt injection')) {
    topics.push('AI security vulnerabilities');
  }
  if (title.includes('Claude') || title.includes('LSP')) {
    topics.push('AI coding assistants');
  }
  if (title.includes('GLM-4.7') || title.includes('GLM')) {
    topics.push('Large language model updates');
  }
  if (title.includes('OpenAI') || title.includes('GPT')) {
    topics.push('OpenAI product updates');
  }
  if (title.includes('Anthropic') || title.includes('Agent')) {
    topics.push('AI agent capabilities');
  }
  if (title.includes('Amazon') || title.includes('$10B')) {
    topics.push('Enterprise AI investments');
  }
  if (title.includes('Google') || title.includes('Gemini')) {
    topics.push('Google AI developments');
  }
  if (title.includes('Meta') || title.includes('SAM')) {
    topics.push('Meta AI research');
  }
  if (title.includes('Nvidia') || title.includes('TPU')) {
    topics.push('AI hardware and compute');
  }
  if (title.includes('Energy') || title.includes('Crisis')) {
    topics.push('AI infrastructure sustainability');
  }
  if (title.includes('EU') || title.includes('Probe') || title.includes('Regulation')) {
    topics.push('AI regulation and compliance');
  }
  
  // Build explanation paragraphs
  let explanation = '';
  
  if (summary.includes('Net:')) {
    const netTakeaway = summary.split('Net:')[1]?.trim() || '';
    explanation += `<p><strong>What this means:</strong> ${netTakeaway}</p>`;
  }
  
  if (topics.length > 0) {
    explanation += `<p>This update covers several key developments in the AI landscape:</p><ul>`;
    topics.forEach(topic => {
      explanation += `<li>${topic}</li>`;
    });
    explanation += `</ul>`;
  }
  
  // Add context based on summary content
  if (summary.includes('policy') || summary.includes('regulation')) {
    explanation += `<p>Policy and regulatory changes continue to shape how AI is deployed and accessed. These developments affect everything from enterprise procurement to startup funding, making it essential to stay informed about the evolving landscape.</p>`;
  }
  
  if (summary.includes('enterprise') || summary.includes('consolidation')) {
    explanation += `<p>Enterprise AI adoption is accelerating, with major deals and partnerships reshaping the competitive landscape. Companies are investing heavily in AI infrastructure and capabilities, signaling a shift toward AI-first business strategies.</p>`;
  }
  
  if (summary.includes('model') || summary.includes('update') || summary.includes('launch')) {
    explanation += `<p>Model updates and new releases are happening at an unprecedented pace. These improvements often bring better performance, new capabilities, and more efficient resource usage, directly impacting what's possible with AI tools.</p>`;
  }
  
  if (summary.includes('security') || summary.includes('risk') || summary.includes('vulnerability')) {
    explanation += `<p>Security considerations remain critical as AI systems become more integrated into production environments. Understanding and mitigating risks is essential for safe AI deployment.</p>`;
  }
  
  if (summary.includes('compute') || summary.includes('infrastructure') || summary.includes('energy')) {
    explanation += `<p>Compute infrastructure and energy consumption are becoming key constraints and differentiators in AI development. Efficient resource usage and sustainable scaling are increasingly important.</p>`;
  }
  
  if (summary.includes('tooling') || summary.includes('workflow') || summary.includes('developer')) {
    explanation += `<p>Developer tooling and workflows are evolving rapidly, making it easier to build and deploy AI-powered applications. Better tooling reduces friction and accelerates development cycles.</p>`;
  }
  
  return explanation || `<p>${summary}</p>`;
}

