// Helper function to extract headings from content
function extractHeadings(content) {
  const headingRegex = /<h([2-3])[^>]*>(.*?)<\/h[2-3]>/gi;
  const headings = [];
  let match;
  
  while ((match = headingRegex.exec(content)) !== null) {
    const level = parseInt(match[1]);
    const text = match[2].replace(/<[^>]*>/g, '').trim();
    const id = text.toLowerCase()
      .replace(/[^\w\s-]/g, '')
      .replace(/\s+/g, '-')
      .replace(/-+/g, '-');
    headings.push({ level, text, id });
  }
  
  return headings;
}

// Helper function to generate table of contents
function generateTOC(headings) {
  if (headings.length < 3) return ''; // Only show TOC if 3+ headings
  
  const tocItems = headings.map(h => {
    const indentClass = h.level === 3 ? 'toc-h3' : 'toc-h2';
    return `<li class="guide-toc-item ${indentClass}"><a href="#${h.id}">${escapeHtml(h.text)}</a></li>`;
  }).join('');
  
  return `
    <div class="guide-toc">
      <div class="guide-toc-title">Table of Contents</div>
      <ul class="guide-toc-list">${tocItems}</ul>
    </div>
  `;
}

// Helper function to add IDs to headings in content
function addHeadingIds(content) {
  if (!content || typeof content !== 'string') return content;
  return content.replace(/<h([2-3])([^>]*)>(.*?)<\/h[2-3]>/gi, (match, level, attrs, text) => {
    if (!text) return match;
    // Check if heading already has an id attribute
    if (attrs && attrs.includes('id=')) {
      return match; // Return as-is if ID already exists
    }
    const id = text.replace(/<[^>]*>/g, '').trim()
      .toLowerCase()
      .replace(/[^\w\s-]/g, '')
      .replace(/\s+/g, '-')
      .replace(/-+/g, '-');
    return `<h${level}${attrs} id="${id}">${text}</h${level}>`;
  });
}

// Helper function to generate one-sentence answer for featured snippets
function generateOneSentenceAnswer(guide) {
  // Try to extract from first paragraph of content (skip headings)
  let contentText = guide.content;
  
  // Remove headings first
  contentText = contentText.replace(/<h[1-6][^>]*>.*?<\/h[1-6]>/gi, '');
  
  // Extract first <p> tag content
  const firstParagraphMatch = contentText.match(/<p[^>]*>(.*?)<\/p>/i);
  if (firstParagraphMatch) {
    let firstPara = firstParagraphMatch[1].replace(/<[^>]*>/g, '').trim();
    // Get first sentence from paragraph
    const firstSentence = firstPara.split(/[.!?]\s+/)[0];
    
    // If first sentence is good length, use it
    if (firstSentence && firstSentence.length > 30 && firstSentence.length < 200) {
      return firstSentence;
    }
    // If paragraph itself is good, use it
    if (firstPara && firstPara.length > 30 && firstPara.length < 200) {
      return firstPara;
    }
  }
  
  // Fallback: extract from all text (skip headings)
  contentText = contentText.replace(/<[^>]*>/g, ' ').replace(/\s+/g, ' ').trim();
  const firstSentence = contentText.split(/[.!?]\s+/)[0];
  
  // If first sentence is too short or too long, generate from question/description
  if (firstSentence && firstSentence.length > 30 && firstSentence.length < 200) {
    return firstSentence;
  }
  
  // Generate from question if available
  if (guide.question) {
    const question = guide.question.toLowerCase();
    
    if (question.includes('how do')) {
      const topic = guide.title.toLowerCase().replace('how do ', '').replace('? a complete guide', '').replace('?', '').replace('how to use ', '').replace('how to ', '');
      return `You use ${topic} by selecting the right tool, preparing your input (text, images, or videos), configuring settings, generating content, and refining the output through iteration.`;
    } else if (question.includes('what is') || question.includes('what are')) {
      const topic = guide.title.replace(/^What (is|are) /i, '').replace('?', '').replace(' A Complete Guide', '');
      return `${topic} ${guide.description.split('.')[0] || 'are AI-powered tools that enable content creation through automated generation.'}`;
    } else if (question.includes('best')) {
      const topic = guide.title.toLowerCase().replace('best ', '').replace(' complete guide', '').replace('?', '');
      return `The best ${topic} are ${guide.description.split('.')[0] || 'tools that match your specific workflow needs, budget, and quality requirements.'}`;
    } else if (question.includes('how to choose')) {
      return `Choose the right AI tool by evaluating your workflow needs, budget, required features, API availability, and generation quality.`;
    }
  }
  
  // Fallback: use description first sentence
  const descFirst = guide.description.split('.')[0];
  if (descFirst && descFirst.length > 30 && descFirst.length < 200) {
    return descFirst + '.';
  }
  
  // Last resort: generate generic answer
  return guide.description.split('.')[0] || null;
}

