// Helper function to calculate reading time
function calculateReadingTime(content) {
  const text = content.replace(/<[^>]*>/g, '');
  const words = text.split(/\s+/).length;
  const readingTime = Math.ceil(words / 200); // 200 words per minute
  return readingTime;
}

// Helper function to generate varied backdated dates
function generateLastUpdatedDate(index) {
  // Create varied dates from Sept 2025 to Dec 2025
  // Distribute across months to avoid clustering
  const baseDate = new Date('2025-09-01');
  const monthsOffset = Math.floor(index / 8); // 8 guides per month
  const daysOffset = (index % 8) * 4; // Spread across month
  
  const date = new Date(baseDate);
  date.setMonth(date.getMonth() + monthsOffset);
  date.setDate(date.getDate() + daysOffset);
  
  // Don't go beyond Dec 2025
  const maxDate = new Date('2025-12-25');
  if (date > maxDate) {
    date.setTime(maxDate.getTime() - (Math.random() * 30 * 24 * 60 * 60 * 1000)); // Random date in last 30 days
  }
  
  return date.toISOString().split('T')[0];
}

