// Identify top comparisons: pair top 2-3 tools in each modality
const comparisons = [];
MODALITIES.forEach(modality => {
  const modalityTools = tools
    .filter(t => t.modalities?.includes(modality))
    .sort((a, b) => (a.curatedRank || 999) - (b.curatedRank || 999))
    .slice(0, 3); // Top 3 tools per modality
  
  // Generate pairs: tool1 vs tool2, tool1 vs tool3, tool2 vs tool3
  for (let i = 0; i < modalityTools.length; i++) {
    for (let j = i + 1; j < modalityTools.length; j++) {
      comparisons.push([modalityTools[i], modalityTools[j]]);
    }
  }
});

// Also add some cross-modality comparisons for popular tools
const popularTools = tools
  .filter(t => (t.curatedRank || 999) <= 20)
  .sort((a, b) => (a.curatedRank || 999) - (b.curatedRank || 999))
  .slice(0, 10);

// Add a few strategic cross-modality comparisons
for (let i = 0; i < Math.min(5, popularTools.length); i++) {
  for (let j = i + 1; j < Math.min(i + 3, popularTools.length); j++) {
    const tool1 = popularTools[i];
    const tool2 = popularTools[j];
    // Only add if they share at least one modality
    if (tool1.modalities?.some(m => tool2.modalities?.includes(m))) {
      const exists = comparisons.some(([t1, t2]) => 
        (t1.id === tool1.id && t2.id === tool2.id) || 
        (t1.id === tool2.id && t2.id === tool1.id)
      );
      if (!exists) {
        comparisons.push([tool1, tool2]);
      }
    }
  }
}

